          <label htmlFor="username">Search for gists by username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
        {gists?.length > 0 && (
          <ul >
            {gists?.map((gist) => (
              <li key={gist?.id}>
                {gist?.description} by {gist?.owner?.login}
              </li>
            ))}
          </ul>
        )}
      </div>
      {/* <InputBox>
        <Input
          placeholder="Search Gists for the username"
