import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Octicon from "react-octicon";
import { getPublicGists } from "../services/gistService";
import { SearchResults } from "./SearchResults";
import { Alert, Spin } from "antd";

export const SearchBar = ({ username }) => {
  const [gists, setGists] = useState([]);
  const [usernames, setUsername] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);

  useEffect(() => {
    getPublicGists()
      .then((response) => {
        console.log(response); // log the response object to the console
        const data = response?.data;
        setGists(data);
        console.log(username, "username");
      })
      .catch((error) => {
        console.error(error);
      });
  }, [username]);

  const onSearch = async (username) => {
    console.log(username, "mew");
    const usersname = username?.trim();
    console.log(username, "mew");
    setUsername(usersname);
    setLoading(true);
    if (usersname && usersname !== "") {
      try {
        const URL = gists(usersname);
        const res = await fetch(URL);
        const data = await res.json();
        setData(data);
        setLoading(false);
        setError(false);
      } catch (e) {
        setLoading(false);
        setError(true);
      }
    } else if (usersname === "") {
      setLoading(false);
      setError(true);
    }
    setLoading(false);
  };
