import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Octicon from "react-octicon";
import { getPublicGists } from "../services/gistService";

export const Search = ({ username }) => {
  const [gists, setGists] = useState([]);

  useEffect(() => {
    getPublicGists(username)
      .then((response) => {
        console.log(response); // log the response object to the console
        const data = response.data;
        setGists(data);
        console.log(data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, [username]);

  const onSearch = async (username) => {
    const usersname = username.trim();
    setUsername(usersname);
    setLoading(true);
    if (usersname && usersname !== "") {
      try {
        const URL = getAllGistUrl(usersname);
        const res = await fetch(URL);
        const data = await res.json();
        setData(data);
        setLoading(false);
        setError(false);
      } catch (e) {
        setLoading(false);
        setError(true);
      }
    } else if (usersname === "") {
      setLoading(false);
      setError(true);
    }
    setLoading(false);
  };
  return (
    <Wrapper>
      <InputBox>
        <ul>
          {gists?.map((gist) => (
            <li key={gist.id}>{gist.description}</li>
          ))}
        </ul>

        <Octicon name="search" />
        <Input placeholder="Search Gists for the username" />
      </InputBox>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  padding: 8px;
  background-color: #ffffff;
  font-size: 14px;
  line-height: 1.5;
  border-radius: 6px;
  margin: 0 16px;
`;

const InputBox = styled.div`
  border-radius: 4px;
  display: flex;
  width: 400px;
`;

const Input = styled.input`
  border: none;
  width: 100%;
  font-size: 16px;

  &:focus {
    outline: 0;
  }
`;

export default Search;
