import React, { useState } from "react";
import { getGistForUser } from "../services/gistService";
import { Card, Button, Tag, Divider,    Space, Col, Row, Avatar } from "antd";
import styled from "styled-components";
import { FileBox } from "./FileBox";
import { Forks } from "./Forks";
import { FileTextOutlined } from "@ant-design/icons";
import Moment from 'react-moment';
export const Cards = (gistData) => {
  const unidata = gistData.gistData;
  console.log(unidata, "unidata");
  const files = unidata.files;
  const fileArr = [];
  for (let file in files) {
    let language = files[file].language;
    //remove duplicate file types
    if (fileArr.indexOf(language) === -1) {
      fileArr.push(language);
    }
  }

  const noOfFiles = Object.keys(files).length;

  const [data, setData] = useState([]);
  const [show, setShow] = useState(false);
  const moreOpen = async (value) => {
    if (value !== "") {
      try {
        const URL = getGistForUser(value);
        const res = await fetch(URL);
        const data = await res.json();
        setData(data);
        setShow(true);
      } catch (e) {
        console.log(e);
        setShow(false);
      }
    }
  };
  const url =
    "https://gw.alipayobjects.com/zos/rmsportal/KDpgvguMpGfqaHPjicRK.svg";

  return (
    <div className="site-card-wrapper">
      <Row>
        <Col span={8}>col-12</Col>
        <Col span={16}>
          <CardContainer
            title={unidata.description || "No Description"}
            extra={
              <Button type="primary" onClick={() => moreOpen(`/${unidata.id}`)}>
                More
              </Button>
            }
          >
            <UserContainer>
              <Space>
                <Avatar
                  src={<img src={`${unidata.owner.avatar_url}`} alt="avatar" />}
                />{" "}
                <Text className="numberFiles">{unidata.owner.login}</Text>
              </Space>
              <List>
                <ListItem>
                  {" "}
                  {noOfFiles} {noOfFiles > 1 ? "Files" : "File"}{" "}
                </ListItem>
                <ListItem>
                  {" "}
                  {show && data !== [] ? <Forks forks={data} /> : null}
                </ListItem>
                <ListItem>
                  {" "}
                  {unidata.comments}{" "}
                  {unidata.comments > 1 ? "Comments" : "Comment"}
                </ListItem>
                <ListItem> Stars</ListItem>
              </List>
            </UserContainer>
            <List>
              <ListItem>
                <h6> Created At: {Moment(unidata.updated_at)} </h6>
              </ListItem>
              <ListItem>
                <h6>Last Update at: {unidata.comments}</h6>
              </ListItem>
            </List>
            <div>
              {fileArr.map((language, index) => {
                return (
                  <Tag color="geekblue" key={index}>
                    {language}
                  </Tag>
                );
              })}
            </div>
            <FileTextOutlined /> <FileBox filelist={files} />
          </CardContainer>
          <Divider type="vertical" />
        </Col>
      </Row>
      <Divider dashed />
    </div>
  );
};

const CardContainer = styled.div`
  border-bottom: 1px solid #d5dce2;
  background-color: #ffffff;
  font-size: 14px;
  margin: 0 16px;
`;
const Text = styled.p`
  color: #000;
  font-size: 14px;
`;

const UserContainer = styled.div`
  color: #000;
  font-size: 14px;
  display: flex;
  justify-content: space-between;
`;
const List = styled.ul`
  color: #000;
  font-size: 14px;
  list-style: none;
`;
const ListItem = styled.li`
  color: #000;
  display: inline-block;
`;
