import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Octicon from "react-octicon";
import { getPublicGists } from "../services/gistService";
import { SearchResults } from "./SearchResults";
import { Input, Alert, Spin } from "antd";
const { Search } = Input;
export const SearchBar = () => {
  const [gists, setGists] = useState([]);
  const [username, setUsername] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const [urls, setURL] = useState([]);
  useEffect(() => {
    getPublicGists()
      .then((response) => {
        console.log(response); // log the response object to the console
        const data = response?.data;
        setGists(data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const onSearch = async (username) => {
    console.log(username, "mew");
    const usersname = username?.trim();
    console.log(username, "mew");
    setUsername(usersname);
    setLoading(true);
    if (usersname && usersname !== "") {
      try {
        getPublicGists(usersname)
          .then((response) => {
            setGists(response);
            console.log(response.url, "response");
            setURL(response.url);
          })
          .catch((error) => {
            console.error(error);
          });
        const URL = getPublicGists(usersname);
        console.log(URL, "URL");
        const res = await fetch(urls);
        console.log(res, "res");
        const data = await res.json();
        console.log(data);

        setData(data);
        console.log(data, "Data");
        setLoading(false);
        setError(false);
      } catch (e) {
        setLoading(false);
        setError(true);
      }
    } else if (usersname === "") {
      setLoading(false);
      setError(true);
    }
    setLoading(false);
  };
  return (
    <Main>
      <Wrapper>
        <InputBox>
          <Search
            placeholder="Search Gists for the username"
            allowClear
            enterButton="Search"
            size="large"
            onSearch={onSearch}
          />
          {loading ? <Spin tip="Loading..." style={{ margin: 10 }} /> : null}

          <Octicon name="search" />
        </InputBox>
      </Wrapper>
      {username !== "" && data && !error ? (
          <div>
            <SearchResults data={data} username={username} />
          </div>
        ) : null}
        <div>
          {username && data?.length === 0 ? (
            <Alert
              message="Error"
              description="No data for this User"
              type="error"
              showIcon
              style={{ marginTop: 10 }}
            />
          ) : null}

          {username === "" ? (
            <Alert
              message="Error"
              description="Please Enter Valid UserName"
              type="error"
              showIcon
              style={{ marginTop: 10 }}
            />
          ) : null}
        </div>
    </Main>
  );
};

const Wrapper = styled.div`
  padding: 8px;
  background-color: #ffffff;
  font-size: 14px;
  line-height: 1.5;
  border-radius: 6px;
  margin: 0 16px;
`;

const InputBox = styled.div`
  border-radius: 4px;
  display: flex;
  width: 400px;
`;

const Main = styled.div`
  padding: 8px;
  background-color: #ffffff;
`;

// const Input = styled.input`
//   border: none;
//   width: 100%;
//   font-size: 16px;

//   &:focus {
//     outline: 0;
//   }
// `;

export default SearchBar;
