import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Octicon from "react-octicon";
import { getPublicGists } from "../services/gistService";
import { SearchResults } from "./SearchResults";
import { Input, Alert, Spin } from "antd";
const { Search } = Input;
export const GistList = (data, username) => {
  const [error, setError] = useState(false);
  return (
    <Main>
      {username !== "" && data && !error ? (
        <div>
          <SearchResults data={data} username={username} />
        </div>
      ) : null}
      <div style={{ position: "absolute" }}>
        {username && data?.length === 0 ? (
          <Alert
            message="Error"
            description="No data for this User"
            type="error"
            showIcon
            style={{ marginTop: 10 }}
          />
        ) : null}

        {username === "" ? (
          <Alert
            message="Error"
            description="Please Enter Valid UserName"
            type="error"
            showIcon
            style={{ marginTop: 10 }}
          />
        ) : null}
      </div>
    </Main>
  );
};

const Main = styled.div`
  padding: 8px;
  background-color: #ffffff;
  font-size: 14px;
  line-height: 1.5;
  border-radius: 6px;
  margin: 0 16px;
`;

const InputBox = styled.div`
  border-radius: 4px;
  display: flex;
  width: 400px;
`;

export default GistList;
