  //     }
  //   } else if (usersname === "") {
  //     setLoading(false);
  //     setError(true);
  //   }
  //   setLoading(false);
  // };
  return (
    <Wrapper>
        <div>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        </label>
        <button type="submit">Get Gists</button>
      </form>
      <ul>
        {gists.map((gist) => (
          <li key={gist.id}>{gist.description}</li>
        ))}
      </ul>
    </div>
      <InputBox>
        <Input
          placeholder="Search Gists for the username"
          allowClear
          enterButton="Search"
          size="large"
          onChange={onSearch}
        />
        {loading ? <Spin tip="Loading..." style={{ margin: 10 }} /> : null}

        {username !== "" && data && !error ? (
          <SearchResults data={data} username={username} />
        ) : null}

        {username && data.length === 0 ? (
          <Alert
            message="Error"
            description="No data for this User"
            type="error"
            showIcon
            style={{ marginTop: 10 }}
          />
        ) : null}

        {username === "" ? (
          <Alert
            message="Error"
            description="Please Enter Valid UserName"
            type="error"
            showIcon
            style={{ marginTop: 10 }}
          />
        ) : null}

        <Octicon name="search" />
      </InputBox>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  padding: 8px;
  background-color: #ffffff;
  font-size: 14px;
  line-height: 1.5;
  border-radius: 6px;
  margin: 0 16px;
`;

const InputBox = styled.div`
  border-radius: 4px;
  display: flex;
  width: 400px;
`;

const Input = styled.input`
  border: none;
  width: 100%;
  font-size: 16px;

  &:focus {
    outline: 0;
  }
`;

export default SearchBar;
