import React, { useState } from "react";
import { getGistForUser } from "../services/gistService";
import { Card, Button, Tag, Divider, Typography } from "antd";
import styled from "styled-components";
import { FileBox } from "./FileBox";
import { Forks } from "./Forks";

export const Cards = (gistData) => {
  const unidata = gistData.gistData;
  console.log(unidata, "unidata");
  const files = unidata.files;
  const fileArr = [];
  for (let file in files) {
    let language = files[file].language;
    //remove duplicate file types
    if (fileArr.indexOf(language) === -1) {
      fileArr.push(language);
    }
  }

  const noOfFiles = Object.keys(files).length;

  const [data, setData] = useState([]);
  const [show, setShow] = useState(false);
  const moreOpen = async (value) => {
    if (value !== "") {
      try {
        const URL = getGistForUser(value);
        const res = await fetch(URL);
        const data = await res.json();
        setData(data);
        setShow(true);
      } catch (e) {
        console.log(e);
        setShow(false);
      }
    }
  };
  return (
    <div className="site-card-wrapper">
      <CardContainer
        title={unidata.description || "No Description"}
        extra={
          <Button type="primary" onClick={() => moreOpen(`/${unidata.id}`)}>
            More
          </Button>
        }
      >
        <UserContainer>
          <Text className="numberFiles">{unidata.owner.login}</Text>
          <List>
            <ListItem>    {noOfFiles} {noOfFiles > 1 ? "Files" : "File"} File </ListItem>
          </List>
        </UserContainer>

        <Text className="numberFiles">
          {noOfFiles} {noOfFiles > 1 ? "Files" : "File"}
        </Text>
        <div>
          {fileArr.map((language, index) => {
            return (
              <Tag color="geekblue" key={index}>
                {language}
              </Tag>
            );
          })}
        </div>

        <FileBox filelist={files} />

        {show && data !== [] ? <Forks forks={data} /> : null}
      </CardContainer>
      <Divider dashed />
    </div>
  );
};

const CardContainer = styled.div`
  border-color: #d5dce2;
  border-bottom: 1px solid;
  background-color: #ffffff;
  font-size: 14px;
  border-radius: 6px;
  margin: 0 16px;
`;
const Text = styled.p`
  color: #000;
  font-size: 14px;
`;

const UserContainer = styled.div`
  color: #000;
  font-size: 14px;
  display: flex;
  justify-content: space-around;
`;
const List = styled.div`
  color: #000;
  font-size: 14px;
  display: flex;
  justify-content: space-around;
`;
const ListItem = styled.div`
  color: #000;
  font-size: 14px;
  display: flex;
  justify-content: space-around;
`;
