import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Octicon from "react-octicon";
import { getPublicGists } from "../services/gistService";
import { GistLists } from "./GistLists";
import { Input, Alert, Spin } from "antd";
const { Search } = Input;
export const SearchBar = () => {
  const [gists, setGists] = useState([]);
  const [username, setUsername] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const [urls, setURL] = useState([]);

  const onSearch = async (username) => {
    const interval = setInterval(50);

    useEffect(() => {
      return () => {
        clearInterval(interval);
      };
    }),
      [];
    const usersname = username?.trim();
    console.log(username, "mew");
    setUsername(usersname);
    setLoading(true);
    if (usersname && usersname !== "") {
      try {
        getPublicGists(usersname)
          .then((response) => {
            setGists(response);
            console.log(response.url, "response");
            setURL(response.url);
          })
          .catch((error) => {
            console.error(error);
          });
        const URL = getPublicGists(usersname);
        const res = await fetch(urls);
        const data = await res.json();
        setData(data);
        setLoading(false);
        setError(false);
      } catch (e) {
        setLoading(false);
        setError(true);
      }
    } else if (usersname === "") {
      setLoading(false);
      setError(true);
    }
    setLoading(false);
  };
  return (
    <Main>
      <Wrapper>
        <InputBox>
          <Search
            placeholder="Search Gists for the username"
            allowClear
            enterButton="Search"
            size="large"
            onSearch={onSearch}
          />
          {loading ? <Spin tip="Loading..." style={{ margin: 10 }} /> : null}

          <Octicon name="search" />
        </InputBox>
      </Wrapper>
      {username !== "" && data && !error ? (
        <div>
          <GistLists data={data} username={username} />
        </div>
      ) : null}
      <div>
        {username && data?.length === 0 ? (
          <Alert
            message="Error"
            description="No data for this User"
            type="error"
            showIcon
            style={{ marginTop: 10 }}
          />
        ) : null}

        {username === "" ? (
          <Alert
            message="Error"
            description="Please Enter Valid UserName"
            type="error"
            showIcon
            style={{ marginTop: 10 }}
          />
        ) : null}
      </div>
    </Main>
  );
};

const Wrapper = styled.div`
  background-color: #000;
  font-size: 14px;
  line-height: 1.5;
  padding: 10px;
  width: 100%;
`;

const InputBox = styled.div`
  border-radius: 4px;
  display: flex;
  // width: 400px;
`;

const Main = styled.div`
  background-color: #ffffff;
  width: 100%;
`;

export default SearchBar;
